import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';

function ElevationScroll(props) {
    const { children, window } = props;

    // useScrollTrigger hook is an event listener for when the user is scrolling
    const trigger = useScrollTrigger({
        // is there a little delay when a user is scrolling
        disableHysteresis: true,
        //0 means as soon as the user starts scrolling, the eventlistener is striggered.
        threshold: 0,
        target: window ? window(): undefined,
    });

    return React.cloneElement(children, {
        elevation: trigger ? 4 : 0
    });
}

export function Navbar(props) {
    return (
        <AppBar position="fixed">
            //toolbar helps stack content horizontally
            <Toolbar>StreamBuddy</Toolbar>
        </AppBar>
    );

}
